import React, { useEffect, useState } from "react";
import "./StyleSheets/JobPostingCard.css"; // Custom CSS for the job posting card
import img from "./img/CompanyLogo.png"; // Replace with your company logo image
import axios from 'axios';

function JobPostingCard() {
  const [Jobs, setJobs] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:3001/showJobs')
      .then(res => {
        setJobs(res.data);
      })
      .catch(err => {
        console.error("Error fetching job data:", err);
      });
  }, []); // Empty dependency array to execute the effect only once when the component mounts

  return (
    <>
      {Jobs.map((job, index) => ( // Add a unique "key" prop to each iterated element
        <div className="card job-posting" key={index}>
          <div className="card-header">
            <h5 className="card-title">{job.title}</h5> {/* Use job title from the data */}
          </div>
          <div className="card-body">
            <div className="row">
              <img src={img} className="job-logo col-3" alt="Company Logo" />
              <div className="col-9">
                <p className="card-text">
                  {job.companyName}
                  <br />
                  {job.location}
                  <br />
                  {job.salary}
                </p>
              </div>
            </div>
            <p className="card-text">
              Job Description:
              <br />
              {job.description}
            </p>
            <p className="card-text">
              Requirements:
              {job.requirement.map((req, index) => ( // Fix typo "requirment" to "requirement"
                <React.Fragment key={index}>
                  <br />
                  {req}
                  <br />
                </React.Fragment>
              ))}
            </p>
          </div>
          <div className="card-footer">
            <a href="#" className="btn btn-primary">
              Apply Now
            </a>
          </div>
        </div>
      ))}
    </>
  );
}

export default JobPostingCard;
