import React from "react";
import "./StyleSheets/JobPostingCard.css"; // Custom CSS for the job posting card

function JobCard({ jobPosting }) {
    const { title, company, location, salary } = jobPosting;
  
    return (
      <div className="card job-posting">
        <div className="card-body">
          <h5 className="card-title">{title}</h5>
          <p className="card-text">
            Company: {company}
            <br />
            Location: {location}
            <br />
            Salary: {salary}
          </p>
          <a href="/api/get" className="btn btn-primary">
            Apply Now
          </a>
        </div>
      </div>
    );
  }

export default JobCard;
