import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import './App.css';
import AdminLogin from './components/AdminLogin';
import JobPostingsList from './components/JobPostingsList';
import JobPostingCard from './components/JobPostingCard';
import NavBar from './components/NavBar';
import { useState } from 'react';
import PostJobs from './components/PostJobs';
import UserLogin from './components/UserLogin';

function App() {
  const [Adminauth, setAdminAuth] = useState(false);
  const [Userauth, setUserAuth] = useState(false);

  const handleAdminAuth = (isAuthenticated) => {
    setAdminAuth(isAuthenticated);
  };


  const handleUserAuth = (isAuthenticated) => {
    setUserAuth(isAuthenticated);
  };
  return (
    <Router>
      <div className='m-auto'>
        <NavBar adminAuth = {Adminauth} userAuth={Userauth} />
        {/* <PostJobs></PostJobs> */}
        <Switch>
          <Route path='/admin-login'>
            <AdminLogin onHandle={handleAdminAuth}  title = 'Admin'/>
          </Route>
          <Route path='/Post-Job'>
            <PostJobs />
          </Route>
          <Route path='/User-login'>
            <UserLogin  onHandle={handleUserAuth}  title = 'User'/>
          </Route>
          <Route path='/full-job'>
            <JobPostingsList />
          </Route>
          <Route path='/Home'>
            <JobPostingCard />
          </Route>
          {/* Default route */}
          <Route path='/'>
            <JobPostingCard />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
