import React, { useState } from "react";
import "./StyleSheets/StudentCard.css";
import img from "./img/Profile.png";

function StudentCard() {
  const studentsPerPage = 5;
  const [currentPage, setCurrentPage] = useState(1);
  const students = [
    {
      name: "Anirudh Kulkarni",
      degree: "EIE",
      skills: "html, css, js, node, react",
      experience: "6 months",
    },
    {
      name: "Jessica Lee",
      degree: "Computer Science",
      skills: "python, java, C++, data structures",
      experience: "1 year",
    },
    {
      name: "Michael Johnson",
      degree: "Mechanical Engineering",
      skills: "CAD, SolidWorks, Matlab, thermodynamics",
      experience: "2 years",
    },
    {
      name: "Sarah Williams",
      degree: "Electrical Engineering",
      skills: "C, C#, VHDL, FPGA",
      experience: "9 months",
    },
    {
      name: "John Smith",
      degree: "Business Administration",
      skills: "marketing, finance, project management",
      experience: "3 years",
    },
    {
      name: "Emily Chen",
      degree: "Chemistry",
      skills: "lab techniques, analytical chemistry",
      experience: "1.5 years",
    },
    {
      name: "David Brown",
      degree: "Biomedical Engineering",
      skills: "biomaterials, medical devices, anatomy",
      experience: "1 year",
    },
    {
      name: "Jennifer White",
      degree: "Mathematics",
      skills: "statistics, calculus, data analysis",
      experience: "2 years",
    },
    {
      name: "Kevin Garcia",
      degree: "Computer Engineering",
      skills: "verilog, FPGA, embedded systems",
      experience: "1.5 years",
    },
    {
      name: "Sophia Miller",
      degree: "English Literature",
      skills: "writing, editing, literary analysis",
      experience: "6 months",
    },
    {
      name: "Daniel Kim",
      degree: "Environmental Science",
      skills: "climate modeling, GIS, sustainability",
      experience: "2 years",
    },
    // Add more student data here...
  ];
  
 // Calculate the index range of students to be displayed on the current page
 const indexOfLastStudent = currentPage * studentsPerPage;
 const indexOfFirstStudent = indexOfLastStudent - studentsPerPage;
 const currentStudents = students.slice(indexOfFirstStudent, indexOfLastStudent);

 const paginate = (pageNumber) => setCurrentPage(pageNumber);

 return (
   <>
     {currentStudents.map((student, index) => (
       <div key={index}>
         <div className="card student">
           <div className="row">
             <img src={img} className="card-img-top col-2" alt="..." />
             <h5 className="col-8">{student.name}</h5>
           </div>
           <div className="card-body">
             <p>Degree: {student.degree}</p>
             <p>Skills: {student.skills}</p>
             <p>Experience: {student.experience}</p>
           </div>
           <div className="accordion" id={`accordionExample${index}`}>
             {/* ... Accordion code for each student (same as the previous example) ... */}
           </div>
         </div>
       </div>
     ))}
     <div className="pagination m-auto">
       {Array.from({ length: Math.ceil(students.length / studentsPerPage) }).map((_, index) => (
         <button key={index} onClick={() => paginate(index + 1)} className={currentPage === index + 1 ? "active" : ""}>
           {index + 1}
         </button>
       ))}
     </div>
   </>
 );
}

export default StudentCard;
