import React, { useEffect, useState } from "react";
import "./StyleSheets/JobPostingCard.css"; // Custom CSS for the job posting card
import img from "./img/CompanyLogo.png"; // Replace with your company logo image
import axios from 'axios';

function JobPostingCard() {

  const [Jobs, setJob] = useState([]);


  

  useEffect(()=>{
    axios.get('http://localhost:3001/showJobs').then(res=>{
      setJob(res.data);
    })
  })

  return (

    <>
    {Jobs.map(job=>{
      return <>
      <div className="card job-posting">
      <div className="card-header">
        <h5 className="card-title">Frontend Developer</h5>
      </div>
      <div className="card-body">
        <div className="row">
          <img src={img} className="job-logo col-3" alt="Company Logo" />
          <div className="col-9">
            <p className="card-text">
              {job.companyName}
              <br />
              {job.location}
              <br />
              {job.salary}
            </p>
          </div>
        </div>
        <p className="card-text">
          Job Description:
          <br />
          {job.description}
        </p>
        <p className="card-text">
          Requirements:

          {job.requirment.map(req=>{
            return <>
            <br/>
              {req}
            <br/>
            </>
          })}
        </p>
      </div>
      <div className="card-footer">
        <a href="#" className="btn btn-primary">
          Apply Now
        </a>
      </div>
    </div>
      </>
    })}
    </>
    
  );
}

export default JobPostingCard;
