import React, { useEffect, useState } from "react";
import "./StyleSheets/JobPostingCard.css"; // Custom CSS for the job posting card
import img from "./img/CompanyLogo.png"; // Replace with your company logo image
import axios from 'axios';

function JobPostingCard() {
  const [Jobs, setJobs] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:3001/showJobs')
      .then(res => {
        console.log(res.data.data);
        setJobs(res.data.data);
      })
      .catch(err => {
        console.error("Error fetching job data:", err);
      });
  }, []);

  return (
    <>
      <div className="job-postings-container">
      {(Jobs.length == 0) ? <>Login first</> :Jobs.map((job, index) => (
        <div className="card job-posting" key={index}>
          <div className="card-header">
            <h5 className="card-title">{job.role}</h5>
          </div>
          <div className="card-body">
            <div className="row">
              <img src={img} className="job-logo col-3" alt="Company Logo" />
              <div className="col-9">
                <p className="card-text">
                  {job.companyName}
                  <br />
                  {job.location}
                  <br />
                  {job.salary}
                </p>
              </div>
            </div>
            <p className="card-text">
              Job Description:
              <br />
              {job.description}
            </p>
            <p className="card-text">
              Requirements:
              {job.requirement.map((req, index) => (
                <React.Fragment key={index}>
                  <br />
                  {req}
                </React.Fragment>
              ))}
            </p>
          </div>
          <div className="card-footer">
            <a href="/" className="btn btn-primary">
              Apply Now
            </a>
          </div>
        </div>
      ))}
    </div>
    </>
  );
}

export default JobPostingCard;
