import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import './App.css';
import AdminLogin from './components/AdminLogin';
import JobPostingsList from './components/JobPostingsList';
import JobPostingCard from './components/JobPostingCard';
import NavBar from './components/NavBar';
import { useState } from 'react';

function App() {

  const [auth , setAuth] = useState(false);

  const HandleAuth = (auth)=>{
    setAuth(true);
  }
  return (
    <Router>
      <div className=' m-auto'>
        <NavBar />
        <Switch>
          <Route path='/admin-login'><AdminLogin onHandle ={HandleAuth}></AdminLogin></Route>
          <Route path='/Home' component={JobPostingsList} />
          <Route path='/Full-Job' component={JobPostingCard} />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
